        int x = int(s1[i]) + f(s1, s2, i+1, j, n , m, dp);
        int y = int(s2[j]) + f(s1, s2, i, j+1, n , m, dp);
        // int z = int(s1[i]) + int(s2[j]) + f(s1, s2, i+1, j+1, n , m);
        return dp[i][j] = min(x, y);
    }

    int minimumDeleteSum(string s1, string s2) {
        int n = s1.length();
        int m = s2.length();
        vector<vector<int>> dp(n, vector<int>(m, -1));
        return f(s1, s2, 0, 0, n, m, dp);
    }
};
