class Solution {
    public int solve(String s1, String s2,int n,int m,int [][]dp)
    {
        if(n==0)
            return dp[n][m]=get(s2,m);
        else if(m==0)
            return dp[n][m]=get(s1,n);

        if(dp[n][m]!=0)
