class Solution {
private:
    int n,m;

    int solver(int i, int j, string& s1, string& s2, int dp[][1001]){

        if(i == n && j == m) return 0;

        if(i == n){
            int cost = 0;
            while(j<m) cost += s2[j++];
            return cost;
        }

        if(j == m){
            int cost = 0;
            while(i < n) cost += s1[i++];
            return cost;
        }

        if(dp[i][j] != -1) return dp[i][j];

        if(s1[i] == s2[j]) return dp[i][j] = solver(i+1,j+1,s1,s2,dp);

        int deli = s1[i] + solver(i+1,j,s1,s2,dp);
        int delj = s2[j] + solver(i,j+1,s1,s2,dp);

        return dp[i][j] = min(deli,delj);
    }
public:
    int minimumDeleteSum(string s1, string s2) {
        n = s1.size();
        m = s2.size();
        int dp[1001][1001];
        memset(dp,-1,sizeof(dp));
        return solver(0,0,s1,s2,dp);
    }
};
