/* DP - TopDown Approach */

func minimumDeleteSum(s1 string, s2 string) int {
    cache := make(map[string]int)
    return findMin(0, 0, s1, s2, cache)
}

func findMin(i, j int, s1, s2 string, cache map[string]int) int {
    if i >= len(s1) && j >= len(s2) {
        return 0
    }

    key := strconv.Itoa(i) + "-" + strconv.Itoa(j)
    if value, ok := cache[key]; ok {
        return value
    }

    res := 0
    if i == len(s1) {
         res = int(s2[j]) + findMin(i, j+1, s1, s2, cache)
    } else if j == len(s2) {
        res = int(s1[i]) + findMin(i+1, j, s1, s2, cache)
    } else if s1[i] == s2[j] {
        res = findMin(i+1, j+1, s1, s2, cache)
    } else {
        res = min(int(s1[i] + s2[j]) + findMin(i+1, j+1, s1, s2, cache), 
        min(int(s2[j]) + findMin(i, j+1, s1, s2, cache), int(s1[i]) + findMin(i+1, j, s1, s2, cache)))
    }
    cache[key] = res
    return res
}

/* DP - BottomUp Approach */

func minimumDeleteSum(s1 string, s2 string) int {
    m := len(s1)
    n := len(s2)
    dp := make([][]int, m+1)
    for r := 0; r <= m; r++ {
        dp[r] = make([]int, n+1)
    }

    for r := 1; r <= m; r++ {
        dp[r][0] = dp[r-1][0] + int(s1[r-1])
    }

    for c := 1; c <= n; c++ {
        dp[0][c] = dp[0][c-1] + int(s2[c-1])
    }

    for r := 1; r <= m; r++ {
        for c:= 1; c <= n; c++ {
            if s1[r-1] == s2[c-1] {
                dp[r][c] = dp[r-1][c-1]
            } else {
                dp[r][c] = min(int(s1[r-1] + s2[c-1]) + dp[r-1][c-1], min(int(s1[r-1]) + dp[r-1][c], int(s2[c-1]) + dp[r][c-1]))
            }
        }
    }

    return dp[m][n]
}


