class Solution {
    public int minimumDeleteSum(String s1, String s2) {
        int[][] dp = new int[s1.length()][s2.length()];
        return findMinimum(s1.toCharArray(), s2.toCharArray(), dp, s1.length() - 1, s2.length() - 1);
    }

    public int findMinimum(char[] s1, char[] s2, int[][] dp, int i, int j) {
        if (i == -1) return calculateSum(s2, j);
        if (j == -1) return calculateSum(s1, i);
        if (dp[i][j] != 0) return dp[i][j];
        int deleteSecond = findMinimum(s1, s2, dp, i, j - 1) + s2[j];
        int deleteFirst = findMinimum(s1, s2, dp, i - 1, j) + s1[i];
        if (s1[i] == s2[j]) {
            int deleteNone = findMinimum(s1, s2, dp, i - 1, j - 1);
            return dp[i][j] =deleteNone;
        }
        return dp[i][j] = Math.min(deleteFirst, deleteSecond);
    }

    public int calculateSum(char[] arr, int index) {
        int sum = 0;
        for (int j = 0; j <= index; j++) sum += arr[j];
        return sum;
    }
