class Solution {
    public int minimumDeleteSum(String s1, String s2) {
        int n = s1.length();
        int m = s2.length();
        int dp[][]=new int [n+1][m+1];
        //base case
        dp[0][0]=0;
        for(int i=1;i<n+1;i++){//when s2 string is empty 
            dp[i][0]=dp[i-1][0]+s1.charAt(i-1);
        }

        for(int i=1;i<m+1;i++){//when s1 string is empty
            dp[0][i]=dp[0][i-1]+s2.charAt(i-1);
        }
        //bottom up approach
        for(int i=1;i<n+1;i++){
            for(int j=1;j<m+1;j++){
                if(s1.charAt(i-1)==s2.charAt(j-1)){
                    dp[i][j]=dp[i-1][j-1];
                }else{
                    int ans1=s1.charAt(i-1)+ dp[i-1][j];
                    int ans2=s2.charAt(j-1)+ dp[i][j-1];
                    dp[i][j]=Math.min(ans1,ans2);
                }
            }
        }
        return dp[n][m];
    }
}
