class Solution {
    public int minimumDeleteSum(String s1, String s2) {
        if(s1.length()==0 && s2.length()==0){
            return 0;
        }
        if(s1.length()==0){
            int sum=0;
            for(int i=0; i<s2.length() ; i++){
                sum+=(int)s2.charAt(i);
            }
            return sum;
        }
        if(s2.length()==0){
            int sum=0;
            for(int i=0; i<s1.length() ; i++){
                sum+=(int)s1.charAt(i);
            }
            return sum;
        }
        if(s1.equals(s2)){
            return 0;
        }
        if(s1.charAt(0)==s2.charAt(0)){
            return minimumDeleteSum(s1.substring(1),s2.substring(1));
        }else{
           int val1=(int)s1.charAt(0);
           int a=val1 + minimumDeleteSum(s1.substring(1),s2);
           int val2=(int)s2.charAt(0);
           int b=val2 + minimumDeleteSum(s1,s2.substring(1));
           return Math.min(a,b);   
        }
    }
}
