class Solution {
    int m,n;
    int mv=0;
    int fun(int i, int j, string &s1, string &s2,vector<vector<int>>&dp)
    {
	   //simple largerst common sum subsequence analogous to lcs 
        if(i==n || j==m)return 0;
        if(dp[i][j]!=-1)return dp[i][j];
        int take=0;
        if(s1[i]==s2[j])
        {
            take +=s1[i]+fun(i+1,j+1,s1,s2,dp);
        }
        else  take+=max(0+fun(i+1,j,s1,s2,dp),0+fun(i,j+1,s1,s2,dp));
        return dp[i][j]=take;
    }
public:
    int minimumDeleteSum(string s1, string s2) {
        n=s1.size();
        m=s2.size();
        int sm1=0;
        for(int i=0;i<n;i++)
        {
            sm1+=s1[i];

        }
        int sm2=0;
        for(int j=0;j<m;j++)
        {
           sm2+=s2[j];
        }
        vector<vector<int>>dp(n,vector<int>(m,-1));
        int lcss= fun(0,0,s1,s2,dp);// lcss will be the max sum value
        int ans = sm1-lcss + sm2 - lcss;//what we delete from each string will be give by total sum - lcss
        return ans;
    }
};
