class Solution {
public:
    int solveRec(int i, int j, string& s1, string& s2)
    {
        if(i == s1.length())
        {
            int ans = 0;
            for(int k=j; k<s2.length(); k++)
            {
                ans += s2[k];
            }
            return ans;
        }
        if(j == s2.length())
        {
            int ans = 0;
            for(int k=i; k<s1.length(); k++)
            {
                ans += s1[k];
            }
            return ans;
        }

        if(s1[i] == s2[j])
            return solveRec(i+1,j+1,s1,s2);
        else
            return min(s1[i]+solveRec(i+1,j,s1,s2), s2[j]+solveRec(i,j+1,s1,s2));    
    }   

    int solveMem(int i, int j, string& s1, string& s2, vector<vector<int>>& dp)
    {
        if(i == s1.length())
        {
            int ans = 0;
            for(int k=j; k<s2.length(); k++)
            {
                ans += s2[k];
            }
            return ans;
        }
        if(j == s2.length())
        {
            int ans = 0;
            for(int k=i; k<s1.length(); k++)
            {
                ans += s1[k];
            }
            return ans;
        }

        if(dp[i][j] != -1)
            return dp[i][j];

        if(s1[i] == s2[j])
            return dp[i][j] = solveMem(i+1,j+1,s1,s2,dp);
        else
            return dp[i][j] = min(s1[i]+solveMem(i+1,j,s1,s2,dp), s2[j]+solveMem(i,j+1,s1,s2,dp));    
    }  

    int minimumDeleteSum(string s1, string s2) {
        //return solveRec(0,0,s1,s2);
        int n = s1.length(), m = s2.length();
        vector<vector<int>> dp(n, vector<int>(m,-1));
        return solveMem(0,0,s1,s2,dp);
    }
};
