class Solution {
public:
    int func(string &s1, string &s2, int idx1, int idx2, vector<vector<int>> &dp){
        if(idx1 >= s1.size() || idx2 >= s2.size()){
            int x=0;
            while(idx1 < s1.size()){
                x += ((int)s1[idx1++]);
            }
            while(idx2 < s2.size()){
                x += ((int)s2[idx2++]);
            }
            return x;
