    int helper(int i, int j, string& s1, string& s2, int size1, int size2){
        if(i == size1 && j == size2)
            return 0;
        if(dp[i][j] != -1)
            return dp[i][j];
        int ans = INT_MAX;
        if(i == size1)
            ans = min(ans,s2[j] + helper(i, j + 1, s1, s2, size1, size2));
        else if(j == size2)
            ans = min(ans,s1[i] + helper(i + 1, j, s1, s2, size1, size2));
