class Solution:
    def minimumDeleteSum(self, s1: str, s2: str) -> int:

        def dfs(i, j):
            # suppose we reach the end of one string, the we have to delete the chars from other string
            if i == len(s1): 
                return sum([ord(c) for c in s2[j:]])

            if j == len(s2):
                return sum([ord(c) for c in s1[i:]])

            if (i, j) in dp:
                return dp[(i, j)]

            res = float('inf')
            if s1[i] == s2[j]: # if both char same nothing to add just increment the pointers
                res = min(res, dfs(i + 1, j + 1))
            else: # else try both ways, del the char from s1 and del the char from s2
                res = min(res, ord(s1[i]) + dfs(i + 1, j))
                res = min(res, ord(s2[j]) + dfs(i, j + 1))
            dp[(i, j)] = res
            return res

        dp = {}
        return dfs(0, 0)

```

