class Solution:
    def minimumDeleteSum(self, s: str, t: str) -> int:
        # finding the lcs
        def lcs(i,j):
            if i == 0 or j == 0: return 0
            if dp[i][j] != -1: return dp[i][j]
            if s[i-1] == t[j-1]: dp[i][j] = 1+lcs(i-1,j-1)
            else: dp[i][j] = max(lcs(i-1,j), lcs(i,j-1))
            return dp[i][j]
        n,m = len(s),len(t)
        dp = [[-1]*(m+1) for _ in range(n+1)]
