class Solution
{
public:
    int f(string s1, int n, string s2, int m, vector<vector<int>> &dp)
    {
        // base case
        if (n < 0 && m < 0)
            return 0;

        //  if one of the string is empty we will delete all the characters of the other string
        if (n < 0)
            return f(s1, n, s2, m - 1, dp) + s2[m];
        //  if one of the string is empty we will delete all the characters of the other string
        if (m < 0)
            return f(s1, n - 1, s2, m, dp) + s1[n];

        // memo : if the value is already calculated we will return it
        if (dp[n][m] != -1)
            return dp[n][m];

        // if the characters are same we will not delete any character
        if (s1[n] == s2[m])
            return dp[n][m] = f(s1, n - 1, s2, m - 1, dp);
        else
        {
            // if the characters are not same we will delete the character which will give us the minimum sum
            return dp[n][m] = min(f(s1, n - 1, s2, m, dp) + s1[n], f(s1, n, s2, m - 1, dp) + s2[m]);
        }

        return 0;
    }
    int minimumDeleteSum(string s1, string s2)
    {
        int n = s1.size() - 1;
        int m = s2.size() - 1;
        vector<vector<int>> dp(n + 1, vector<int>(m + 1, -1));
        return f(s1, n, s2, m, dp);
    }
};
