class Solution {
public:
    void findLCS(string &s1, string &s2, int i, int j, string s, int &minAns, long long int &sum1, long long int &sum2, int &maxLen) {
        if(i == s1.size() || j == s2.size()) {
            // cout << s << endl;
            int size = s.size();
            if(size == 0) return;
            else if(size >= maxLen)
            {
                long long int sum = 0;
                for(char ch : s) sum += ch;
                // cout << sum << endl;
                sum = (sum1 + sum2 - 2 * sum);
                if(minAns > sum) minAns = sum;
                maxLen = s.size();
            }
            return;
        }

        if(s1[i] == s2[j]) findLCS(s1, s2, i + 1, j + 1, s + s1[i], minAns, sum1, sum2, maxLen);
        else {
            findLCS(s1, s2, i + 1, j, s, minAns, sum1, sum2, maxLen);
            findLCS(s1, s2, i, j + 1, s, minAns, sum1, sum2, maxLen);
        }
    }
    int minimumDeleteSum(string s1, string s2) {
        int minAns = INT_MAX;
        long long int sum1 = 0, sum2 = 0;
        for(char c : s1) {
            sum1 += c;
        }
        for(char c : s2) {
            sum2 += c;
        }
        // cout << sum1 << sum2 << endl;
        int maxLen = INT_MIN;
        findLCS(s1, s2, 0, 0, "", minAns, sum1, sum2, maxLen);
        if(minAns == INT_MAX) return sum1 + sum2;
        return minAns;
    }
};

