    def minimumDeleteSum(self, s1: str, s2: str) -> int:
        cost, _ = self.m_d_s(s1,s2)
        return cost

    @cache
    def m_d_s(self,s1,s2):
        if s1 == s2:
            return 0, True
        if s1 and not s2:
            return -1, False
        if s2 and not s1:
            return -1, False
        costs = list()
        for i in range(len(s1)):
            rem = s1[i+1:] if i < len(s1) - 1 else ""
            cost, eq = self.m_d_s(s1[:i] + rem,s2)
            if eq:
                cost += ord(s1[i])
                costs.append(cost)
            cost = 0
        for i in range(len(s2)):
            rem = s2[i+1:] if i < len(s2) - 1 else ""
            cost, eq = self.m_d_s(s1, s2[:i] + rem)
            if eq:
                cost += ord(s2[i])
                costs.append(cost)
            cost = 0
        if costs:
            return min(costs), True
        return -1, False
