    int md(int fs,int ss,string s1,string s2,vector<vector<int>> &dp)
    {
        int m;
        if(fs<0 && ss<0)
        {
            return 0;
        }
        else if(fs<0 && ss>=0)
        {
            // m= s2[ss] + md(fs,ss-1,s1,s2,dp);
            return val(s2,ss);
        }
        else if(ss<0 && fs>=0)
        {
            // m= s1[fs] + md(fs-1,ss,s1,s2,dp);
            return val(s1,fs);
        }
        else if(dp[fs][ss]!=-1)
        {
            return dp[fs][ss];
        }
        else if(s1[fs]==s2[ss])
        {
            m= md(fs-1,ss-1,s1,s2,dp);
        }
        else{
            m= min( int(s1[fs]) + md(fs-1,ss,s1,s2,dp) , int(s2[ss]) +md(fs,ss-1,s1,s2,dp) );
        }

        return dp[fs][ss]=m;
    }

    int minimumDeleteSum(string s1, string s2) {

        int fs=s1.size();
        int ss=s2.size();

        vector<vector<int>> dp(fs,vector<int>(ss,-1));

        int val=md(fs-1,ss-1,s1,s2,dp);
        return val;
    }

