        }
        for (int i = w1.length - 1; i > -1; i--) {
            last = w1.length - i;
            diag = w1.length - 1 - i;
            for (int j = w2.length - 1; j > -1; j--) {
                int tmp = dp[j];
                if (w1[i] == w2[j]) {
                    last = dp[j] = diag;
                } else {
                    last = dp[j] = Math.min(diag, Math.min(dp[j], last)) + 1;
                }
                diag = tmp;
            }
        }
        return last;
    }
}
