**Space Optimization**
```
class Solution {
public:
    // Space Optimization - TC: O(n * m), SC: O(m)
    int minDistance(string word1, string word2) {
    int n = word1.size(), m = word2.size();
    // We make dp array 1 indexed
    // Initialize two vectors to store the values of the previous and current rows of the dp array
    vector<int> prev(m+1, 0), curr(m+1, 0);
    // Initialize the first row of the dp array to the numbers 0, 1, 2, ..., m
    for(int j=0;j<=m;j++) prev[j] = j;
    // Iterate through each row of the dp array
    for(int i=1;i<=n;i++){
        // Set the first element of the current row to i
        curr[0] = i;
        // Iterate through each column of the dp array
        for(int j=1;j<=m;j++){
            // If the two characters are equal, no operation is needed
            if(word1[i-1] == word2[j-1])
                curr[j] = prev[j-1];
            else {
                // If the two characters are different, consider three possible operations:
                // Insertion: add 1 to the minimum value among prev[j], curr[j-1], and prev[j-1]
                curr[j] = 1 + min(prev[j], min(curr[j-1], prev[j-1]));
            }
        }
        // Update the previous row to be equal to the current row
        prev = curr;
    }
    // Return the value of the last element of the dp array, which represents the minimum edit distance
    return prev[m];
}
};
