        int[][] arr = new int[word1.length()+1][word2.length()+1];
        //set the boundary conditions.
        for (int i = 0; i<arr.length; i++) {
            arr[i][0] = i; 
        }

        for (int j = 0; j<arr[0].length; j++) {
            arr[0][j] = j; 
