class Solution {
public:
    int solve(int i, int j, string &s1, string &s2){
        //Base Case
        if(i<0) return j + 1;
        if(j<0) return i + 1;
        if(s1[i]==s2[j]) return solve(i-1,j-1,s1,s2);

        //Insert the matching character so reduce the j no effect on i due to insertion
        int ins = solve(i,j-1,s1,s2); 

        // Delete the character so reduce the i but no effect on j
        int del = solve(i-1,j,s1,s2); 

        // Replace with matching character so reduce i and j both
        int rep = solve(i-1,j-1,s1,s2);

        return 1 + min({ins, del, rep});
    }

    int minDistance(string s1, string s2) {
        int m = s1.size(), n = s2.size();
        return solve(m,n,s1,s2);
    }
};
