    {
        if (i == word1.size()) return word2.size() - j;
        if (j == word2.size()) return word1.size() - i;
        if( s1[i] == s2[j] ) return editDistance(i+1 , j+1, s1, s2);
        return 1 + min( editDistance(i , j+1, s1, s2), min( editDistance(i+1 , j, s1, s2),  editDistance(i+1 , j+1, s1, s2)));  // Didn't Got it , No issue again go through the explanation !
    }

