class Solution {
    public int minDistance(String word1, String word2) {
        final int m = word1.length();
        final int n = word2.length();
        final int[][] memo = new int[m][n];

        return minDistHelper(word1, word2, m, n, memo);
    }

    private int minDistHelper(String word1, String word2, int m, int n, int[][] memo) {

        if(m == 0) {
            return n;
        }

        if(n == 0) {
            return m;
        }

        if(memo[m - 1][n - 1] != 0) {
            return memo[m - 1][n - 1];
        }

        if(word1.charAt(m - 1) == word2.charAt(n - 1)) {
            return minDistHelper(word1, word2, m - 1, n - 1, memo);
        }

        int insert = minDistHelper(word1, word2, m, n - 1, memo);
        int remove = minDistHelper(word1, word2, m - 1, n, memo);
        int replace = minDistHelper(word1, word2, m - 1, n - 1, memo);

        memo[m - 1][n - 1] = 1 + Math.min(insert, Math.min(remove, replace));
        return memo[m - 1][n - 1];
    }
}
