class Solution {
public:
    using VI = vector<int>;
    using VVI = vector<VI>;
    int minDistance(string A, string B) {
        auto [M, N] = make_tuple(A.size(), B.size());
        VVI dp(M + 1, VI(N + 1)); // 🤔 memo
        for (auto i = 0; i <= M; ++i) dp[i][0] = i; // 🛑 base case: we have exhausted B, return "leftover" distance
        for (auto j = 0; j <= N; ++j) dp[0][j] = j; // 🛑 base case: we have exhausted A, return "leftover" distance
        for (auto i = 1; i <= M; ++i)
            for (auto j = 1; j <= N; ++j)
                dp[i][j] = min({
                    dp[i - 1][j - 1] + int(A[i - 1] != B[j - 1]), // ✅ match xor ❌ mismatch
                    dp[i - 1][j] + 1, // ❌ insertion 
                    dp[i][j - 1] + 1, // ❌ deletion
                });
        return dp[M][N];
    }
};
```

*C++: Bottom-Up with memory optimization to only store previous and current rows of the DP matrix*
```
class Solution {
public:
    using VI = vector<int>;
    using VVI = vector<VI>;
    int minDistance(string A, string B) {
        auto [M, N] = make_tuple(A.size(), B.size());
        VI pre(N + 1); // 🤔 memo
        iota(pre.begin(), pre.end(), 0); // 🛑 base case: we have exhausted A or B, i is the "leftover" distance
        for (auto i = 1; i <= M; ++i) {
            auto cur{ pre }; // 🤔 generate current from previous
            cur[0] = i; // 🛑 base case: we have exhausted A or B, i is the "leftover" distance
            for (auto j = 1; j <= N; ++j) {
                cur[j] = min({
                    pre[j - 1] + int(A[i - 1] != B[j - 1]), // ✅ match xor ❌ mismatch
                    pre[j] + 1,     // ❌ insertion 
                    cur[j - 1] + 1, // ❌ deletion
                });
            }
            swap(pre, cur);
        }
        return pre[N];
    }
};
