class Solution {
public:
     int LCS(string x,string y, int n, int m){
        int dp[n+1][m+1];
        for(int i =0;i<n+1;i++){
            dp[i][0]=i;
        }
        for(int j=0;j<m+1;j++){
            dp[0][j]=j;
        }


        for(int i=1;i<n+1;i++){
            for(int j=1;j<m+1;j++){
                if(x[i-1]== y[j-1]){
                    dp[i][j]= dp[i-1][j-1];
                }
                else{
                    dp[i][j] = 1+min(dp[i-1][j],min(dp[i][j-1],dp[i-1][j-1]));
                }
            }
        }

        return dp[n][m];   

    }
    int minDistance(string word1, string word2) {
        int n = word1.size();
        int m = word2.size();

        return LCS(word1,word2,n,m);
    }
};
