```
    int minDistance(string word1, string word2) {
        int size1 = word1.size();
        int size2 = word2.size();
        int row = size1 + 1;
        int column = size2 + 1;
        vector<vector<int>> lm(row, vector<int>(column, 0));
        // the distance between "" and "" is zero
        lm[0][0] = 0;
        // the operations of converting "" to a length i word
        for (int i = 1; i < column; i++) {
            lm[0][i] = i;
        }
        // the operations of converting a length i word to ""
        for (int i = 1; i < row; i++) {
            lm[i][0] = i;
        }

        for (int r = 1; r < row; r++) {
            for (int c = 1; c < column; c++) {
                // if the last character of two strings are the same, then the distance is the same as lm[r-1][c-1]
                if (word1[r - 1] == word2[c - 1]) {
                    lm[r][c] = lm[r - 1][c - 1];
                } else {
                    // if we have known the min distance between s1[1...r-1] and s2[1...c-1]
                    // then we can replace s1[r] with s2[c] first then convert s1[1...r-1] to s2[1...c-1]
                    int replace = lm[r - 1][c - 1] + 1;
                    // if we have known the min distance bwtween s1[1...r-1] and s2[1...c]
					// we can  delete s1[r] first then convert s1[1...r-1] to s2[1...c]
                    int del = lm[r - 1][c] + 1;
                    // if we have known the min distance between s1[1...r] and s2[1...c-1]
					// we can convert s1[1...r] to s2[1...c-1] then insert s2[c] to the end of s1
                    int insert = lm[r][c - 1] + 1;
                    // find the min value among the above operation steps
                    int result = min(replace, del);
                    result = min(result, insert);
                    lm[r][c] = result;
                }
            }
        }
        return lm[size1][size2];
    }
