class Solution {
    public int minDistance(String word1, String word2) {

        int n = word1.length();
        int m = word2.length();

        int[][] memo = new int[n+1][m+1];
        for(int[] mem : memo){
            Arrays.fill(mem, -1);
        }
        return Helper(word1, word2, n, m, memo);       

    }

    public static int Helper(String word1, String word2, int n, int m, int[][] memo){

        if(memo[n][m] != -1){
            return memo[n][m];
        }

        if(n == 0){
            return m;
        }
        if(m == 0){
            return n;
        }

        if(word1.charAt(n-1) == word2.charAt(m-1)){
            return memo[n][m] = Helper(word1, word2, n-1, m-1, memo);
        }
        else{
            int delete = 1 + Helper(word1, word2, n-1, m, memo);  // delete operation
            int replace = 1 + Helper(word1, word2, n-1, m-1, memo);  // replace operation
            int insert = 1 + Helper(word1, word2, n, m-1, memo); // insert operation

            return memo[n][m] = Math.min(delete, Math.min(replace, insert));
        }        

    }

}
