    int solve(int n, int m, string word1, string word2, vector<vector<int>> &dp){
        if(n==0 || m==0) return max(n,m);
        if(dp[n][m]!=-1) return dp[n][m];
        if(word1[n-1]==word2[m-1]) return solve(n-1,m-1,word1,word2,dp);
        else{
            dp[n][m] = min(solve(n-1,m,word1,word2,dp),
                           min(solve(n-1,m-1,word1,word2,dp),
                               solve(n,m-1,word1,word2,dp))) +1;
            return dp[n][m];
        }
    }
};
