int findDuplicate(vector<int>& nums) {
  int lo = 0;
  int hi = nums.size() - 1;
  int mid;
  while (lo < hi) {
    mid = lo + (hi - lo) / 2;
    // Count how many numbers are equal or below the mid, if it is lower than expected then the
    // duplicate must be in the upper partition, other wise it's in the lower partition.
    if (mid >= std::count_if(nums.begin(), nums.end(), [&mid] (int n) { return n <= mid; })) {
      lo = mid + 1;
    } else {
      hi = mid;
    }
  }
  return hi;
}
