int findDuplicate(int[] nums) {
        int slow = 0, fast = 1;
        int n = nums.length;
        while (nums[slow] != nums[fast]) {
            slow = (slow + 1) % n;
            fast = (fast + 2) % n;
            if (fast == slow) fast = (fast + 1) % n;
        }
