    int findDuplicate(vector<int>& nums) {

        int n = nums.size();
        for(int i=0;i<n;i++)
            while(nums[i]!=nums[nums[i]-1])
                swap(nums[i],nums[nums[i]-1]);
        return nums[n-1];

    }
};
