class Solution {

    private boolean isDuplicateRange(int l, int r, int[] nums) {
        int count = 0;
        for (int i = 0; i < nums.length; ++i) {
            if (nums[i] >= l && nums[i] <= r)
                ++count;
        }
        return r - l + 1 < count;
    }

    public int findDuplicate(int[] nums) {
        int N = nums.length - 1;
        int l = 1;
        int r = N;
        while (l < r) {
            int mid = l + (r - l) / 2;
            if (isDuplicateRange(l, mid, nums)) {
                r = mid;
            } else {
                l = mid + 1;
            }
        }
        return l;
    }
}
