```
class Solution {
public:
    int pivotIndex(vector<int>& nums) {
        int totalSum = 0;
        for(int j = 0; j < nums.size(); j++){
            totalSum  += nums[j];
        }
        int leftSum = 0, rightSum = totalSum  - nums[0];
        int i = 1;
        while(leftSum != rightSum && i < nums.size()){
            leftSum += nums[i-1];
            rightSum -= nums[i];
            i++;
        }
        int ans = (leftSum == rightSum) ? i-1 : -1;
        return ans;
    }
};
