class Solution {
public:
    int pivotIndex(vector<int>& v) {
        int n = v.size();
        vector<int> left(n);
        vector<int> right(n);

        left[0] = v[0];
        for(int i = 1;i < n;i++) left[i] = left[i - 1] + v[i];

        right[n - 1] = v[n - 1];
        for(int i = n - 2;i >= 0;i--) right[i] = right[i + 1] + v[i];

        for(int i = 0;i < n;i++){
            if(left[i] == right[i]) return i;
        }
        return -1;
    }
};
