class Solution {
public:
    int pivotIndex(vector<int>& nums) {
        int totalSum=0;
        int len=nums.size();
        for(int i=0;i<len;i++){
            totalSum+=nums[i];
        }
        int leftSum=0
        for(int i=0;i<len;i++){
            leftSum+=nums[i];
            if(totalSum-leftSum==leftSum-nums[i])
                return i;
        }
        return -1;
    }
};
```

