class Solution {
public:
    int pivotIndex(vector<int>& nums) {

        // getting sum of all elements in nums
        // this is the current element value + sum of elements to the right
        int right = accumulate(nums.begin(), nums.end(), 0);    

        // left holds sum of elements to the left
        // current element added to left inside loop
        int left = 0;

        // index holds the index of the current element
        int index = 0;

        // finding pivot
        for(int i=0; i<nums.size(); i++){
            // adding current value to left
            left += nums[i];
            // if( current element + left sum == current element + left sum)
            // return index of current element
            if(left==right) return index;

            right -= nums[i];    
            index++;
        }
        return -1;
    }
};
```

