class Solution {
public:
    int pivotIndex(vector<int>& nums) {
		// Set values of easch element with their prefix sum
        for(int i=1; i<nums.size(); i++) nums[i] += nums[i-1];

		// Inorder to left-most we have to start from 0
        if(nums[nums.size()-1] - nums[0] == 0) return 0;

		// Then for 1 to n-2
        for(int i=1; i<nums.size()-1; i++)
            if(nums[nums.size()-1]-nums[i] == nums[i-1]) return i;

		// And at last for n-1
        if(nums[nums.size()-2] == 0) return nums.size()-1;

		// Ther is no pivot index possible
        return -1;
    }
};
```
