        for (int i = n - 2; i >= 0; i--) {
            rl[i] = rl[i + 1] + nums[i + 1];
        }

        for (int i = 0; i < n; i++) {
            if (lr[i] == rl[i]) {
                return i;
            }
        }
        return -1;
    }
