class Solution {
public:
    int pivotIndex(vector<int>& nums) {
        int n  = nums.size();
        // first find total sum of all the elements
        int total = 0;
        for(int i:nums)
        {
            total = total+i;
        }
        int leftSum = 0;
        int rightSum;
        for(int i=0;i<n;i++)
        {
            rightSum = total-nums[i]-leftSum;

            if(leftSum==rightSum)
            {
            return i;
            }
            leftSum = leftSum+nums[i];
        }
        return -1;
    }
};

