        }
        int leftSum = 0;
        int rightSum;
        for(int i=0;i<n;i++)
        {
            rightSum = total-nums[i]-leftSum;

            if(leftSum==rightSum)
            {
            return i;
            }
            leftSum = leftSum+nums[i];
        }
        return -1;
    }
};

