class Solution {
public:
    int pivotIndex(vector<int>& nums) {
        int sum = 0;
        for(int i = 0;i<nums.size();i++){
            sum+=nums[i];
        } 

        int rs = 0,ls = 0; 
        for(int i = 0; i<nums.size();i++){
            int rs = sum-nums[i]-ls;
            if(rs == ls){
                return i;
            }
            ls+=nums[i];
        } 
        return -1;    
    }
};

