class Solution {
public:
    int pivotIndex(vector<int>& nums) {
        int ans = -1;
        for(int i = 0; i < nums.size(); i++){
            int sr = 0;
            int sl = 0;
            for(int j=0;j<nums.size();j++){
                if(j<i){
                    sl = sl + nums[j];
                }
                else if(j>i){
                    sr = sr + nums[j];
                }
            }
            if(sr == sl){
                ans = i;
                break;
            }
        }
        return ans;

    }
};

