class Solution {
public:
    int pivotIndex(vector<int>& nums) {
        unordered_map<int, int> leftSum;
        unordered_map<int, int> rightSum;

        int temp = 0;
        for(int i=0; i<nums.size(); i++) {
            leftSum[i] = temp;
            temp += nums[i];
        }

        temp = 0;
        for(int j=nums.size()-1; j>=0; j--) {
            rightSum[j] = temp;
            temp += nums[j];
        }  
        for(int i=0; i<nums.size(); i++) {
            if(leftSum[i] == rightSum[i]) return i;
        }
        return -1;
    }
};

