class Solution {
    public int pivotIndex(int[] nums) {
        int nextSum = 0;
        int prevSum = 0;
        for(int i=0;i<nums.length;i++){
            nextSum += nums[i];
        }
        for(int i=0;i<nums.length;i++){
            nextSum -= nums[i];
            if(i>0){
                prevSum += nums[i-1];
            }
            if(nextSum==prevSum){
                return i;
            }
        }
        return -1;
    }
}

