```c++ []
class Solution {
public:
    char binarySearch(vector<char>& letters,char target,int s,int e){
        while(s<=e){
            int mid=s+(e-s)/2;
            if(letters[mid]<=target){
                s=mid+1;
            }
            else{
                e=mid-1;
            }
        }
        return s<letters.size()?letters[s]:letters[0];
    }
    char nextGreatestLetter(vector<char>& letters, char target) {
        char c=binarySearch(letters,target,0,letters.size()-1);
        return c;
    }
};
