class Solution {
public:
    char nextGreatestLetter(vector<char>& letters, char target) {
        int i = 0, j = letters.size()-1; 
        char ans = letters[0]; //if no greater find the default ans would be first letter, as asked in question
        while(i<=j){
            int mid = (i+j)/2;
            if(letters[mid]>target){ //if the mid char greater than target then it can be an answer but we also search behind it if any smaller ans can be found
                ans = letters[mid];
                j = mid-1;
            }
