class Solution {
public:
    char nextGreatestLetter(vector<char>& letters, char target) {

    // binary search
    int n=letters.size();
    int start = 0;
    int end = n - 1;
    char ans = letters[0];
    if(letters[end]<=target)
        return ans;
    while (start < end)
    {
        int mid = start + (end - start) / 2;
        if (letters[mid] == target || letters[mid] < target)
        {
            start = mid + 1;
            ans=letters[start];
        }
        else if (letters[mid] > target)
        {
            end = mid;
            ans = letters[end];
        }
    }
    return ans;

    }
};
```
