class Solution {
public:
    char nextGreatestLetter(vector<char>& l, char target) {
    // Initialize ans value to first letter as given in question 
    //  it will be our alternate solution    
    char ans=l[0];

        //create a diff int to check difference between two char
        //difference show how much far one char is to another char
        int diff=INT_MAX;//set it to max as we dont know the diff yet so any diff will always be less than it in start

        for(int i=0;i<l.size();i++){
            //calulating difference between for every char in letter vector to given target
