class Solution {
    public char nextGreatestLetter(char[] letters, char target) {
        // initialised starting and ending variables
        int start = 0, end = letters.length-1; 

        // loop for iterations
        while(start<=end){
            // using Binary search So.
            int mid = start + (end - start)/2;// getting mid value of that array
            // Now we have to get the element just greater then the target character so if mid value of letters array is greater then targer means closest greater char is on left side. so we dercrement end pointer and set to mid-1
            if(letters[mid]>target){
                end = mid - 1;
            }
            // if target is bigger than the letters[mid] value then our closest greater number is on right side of an array. so increment the starting pointer to right.
            else{
                start = mid + 1;
            }
        }
        // at last we focus on starting pointer as we are increment it mid+1 so we take modulus of start pointer with the length of letters array. It give the poistion of that character and return that element from letters array letters[position_of_closest_greater_number]
        return letters[start % letters.length];
    }
}
