                } else {
                    for (int j = m-1; j >= 0; j--) {
                        match[j+1] = (p.charAt(i) == '?' || p.charAt(i) == s.charAt(j)) && match[j];
                    }
                    match[0] = false;
                }
            }
            return match[m];
